import { EventEmitter } from "@angular/core";
import { recipe } from "./recipe.model";


export class RecipesService {
  RecipeSelected = new EventEmitter<recipe>();


  private recipes: recipe[] = [
    new recipe({ name: 'Pav Bhaji1', desc: 'This recipe for pav bhaji. the best street food', image: 'https://www.indianhealthyrecipes.com/wp-content/uploads/2021/11/pav-bhaji.jpg', Ingredient: [] }),
    new recipe({ name: 'Vegetables', desc: 'Vegetables are parts of plants that are consumed by humans or other animals as food.', image: 'https://blogs.biomedcentral.com/on-medicine/wp-content/uploads/sites/6/2019/09/iStock-1131794876.t5d482e40.m800.xtDADj9SvTVFjzuNeGuNUUGY4tm5d6UGU5tkKM0s3iPk-620x342.jpg', Ingredient: [] }),
  ];
  getRecipes() {
    return this.recipes.slice();
  }
}
