import { Component, OnInit, EventEmitter, Output } from '@angular/core';
import { recipe } from '../recipe.model'
@Component({
  selector: 'app-recipes-list',
  templateUrl: './recipes-list.component.html',
  styleUrls: ['./recipes-list.component.css']
})
export class RecipesListComponent implements OnInit {
  @Output() recipewasSelcted = new EventEmitter<recipe>();

  recipeName = '';
  recipeDesc = '';
  recipeImagePath = '';

  recipes: recipe[] = [
    new recipe('Pav Bhaji1', 'This recipe for pav bhaji. the best street food',
      'https://www.indianhealthyrecipes.com/wp-content/uploads/2021/11/pav-bhaji.jpg'),
    new recipe('Vegetables', 'Vegetables are parts of plants that are consumed by humans or other animals as food.',
      'https://blogs.biomedcentral.com/on-medicine/wp-content/uploads/sites/6/2019/09/iStock-1131794876.t5d482e40.m800.xtDADj9SvTVFjzuNeGuNUUGY4tm5d6UGU5tkKM0s3iPk-620x342.jpg'),
  ];
  ngOnInit(): void {
    // console.log(this.recipes);
  }
  onrecipeSelected(recipe: recipe) {
    this.recipewasSelcted.emit(recipe);
  }
  newRecipes() {

  }
}
