import {
  Directive,
  HostListener,
  HostBinding
} from "@angular/core";

@Directive({
  selector: "[appDropdown]",
  // exportAs: "appDropDown"
})
export class DropdownDirective {
  private wasInside = false;

  @HostBinding("class.show") isOpen = false;

  @HostListener("click") toggleOpen() {
    this.isOpen = !this.isOpen;
    this.wasInside = true;
  }

